    public OATree createSearchTree() {
        OATree tree = new OATree();
        tree.setPreferredSize(10, 18);
        OATreeTitleNode tnode;
        OATreeNode node, node2;
    
        tnode = new OATreeTitleNode(getModel().getPluralDisplayName());
        tnode.setIcon(AppServerJfc.getTitleIcon());
        tree.add(tnode);
        node = new OATreeNode(AppServerJfc.PP_Display, ModelDelegate.getAppServers().createSharedHub(), getHub()) {
            @Override
            public void onDoubleClick(Object obj, MouseEvent e) {
                AppServerJfcBase.this.onDoubleClickTreeNode();
            }
        };
        if (getModel().getAllowMultiSelect()) {
            node.setSelectedHub(getModel().getMultiSelectHub());
        }
        node.setIcon(AppServerJfc.getIcon());
        node.setForegroundColorProperty(AppServerJfc.PP_ForeColor);
        node.setBackgroundColorProperty(AppServerJfc.PP_BackColor);
        node.setIconColorProperty(AppServerJfc.PP_IconColor);
        node.setImageProperty(AppServerJfc.PP_Icon);
        node.setFontProperty(AppServerJfc.PP_Font);
        node.setMaxImageHeight(20);
        node.setMaxImageWidth(32);
        if (getModel().getAllowMultiSelect()) {
            node.setSelectedHub(getModel().getMultiSelectHub());
        }
        tnode.add(node);
        return tree;
    }
    
    
