    protected void onHubSearch() {
        getSearchHubJfc().getDialog().setVisible(true);
        if (!getSearchHubJfc().wasSelected()) return;
        if (getModel().getAllowMultiSelect()) {
            getModel().getMultiSelectHub().clear();
            for (AppServer appServer : getSearchHubJfc().getModel().getMultiSelectHub()) {
                if (getHub().contains(appServer)) {
                    getModel().getMultiSelectHub().add(appServer);
                }
            }
            getHub().setAO(getModel().getMultiSelectHub().getAt(0));
        }
        else {
            AppServer appServer = getSearchHubJfc().getSelected();
            if (appServer != null) {
                if (getHub().contains(appServer)) {
                    getHub().setAO(appServer);
                }
            }
        }
    }
