    public OATree createSearchTree() {
        OATree tree = new OATree();
        tree.setPreferredSize(10, 32);
        OATreeTitleNode tnode;
        OATreeNode node, node2;
    
        // AppUser.appUserLogins
        tnode = new OATreeTitleNode("App Users");
        tnode.setIcon(AppUserJfc.getTitleIcon());
        tree.add(tnode);
        node = new OATreeNode(AppUserJfc.PP_Display, ModelDelegate.getAppUsers().createSharedHub());
        node.setIcon(AppUserJfc.getIcon());
        node.setForegroundColorProperty(AppUserJfc.PP_ForeColor);
        node.setBackgroundColorProperty(AppUserJfc.PP_BackColor);
        node.setIconColorProperty(AppUserJfc.PP_IconColor);
        node.setImageProperty(AppUserJfc.PP_Icon);
        node.setFontProperty(AppUserJfc.PP_Font);
        node.setMaxImageHeight(20);
        node.setMaxImageWidth(32);
        tnode.add(node);
        node2 = new OATreeNode(OAString.cpp(AppUser.P_AppUserLogins, PP_Display), getHub());
        node2.setIcon(getIcon());
        node2.setFont(tree.getFont().deriveFont(Font.BOLD));
        node2.setForegroundColorProperty(PP_ForeColor);
        node2.setBackgroundColorProperty(PP_BackColor);
        node2.setIconColorProperty(PP_IconColor);
        node2.setImageProperty(PP_Icon);
        node2.setFontProperty(PP_Font);
        node2.setMaxImageHeight(20);
        node2.setMaxImageWidth(32);
        if (getModel().getAllowMultiSelect()) {
            node2.setSelectedHub(getModel().getMultiSelectHub());
        }
        node.add(node2);
        return tree;
    }
    
    
