    protected void onHubSearch() {
        getSearchHubJfc().getDialog().setVisible(true);
        if (!getSearchHubJfc().wasSelected()) return;
        if (getModel().getAllowMultiSelect()) {
            getModel().getMultiSelectHub().clear();
            for (AppUserLogin appUserLogin : getSearchHubJfc().getModel().getMultiSelectHub()) {
                if (getHub().contains(appUserLogin)) {
                    getModel().getMultiSelectHub().add(appUserLogin);
                }
            }
            getHub().setAO(getModel().getMultiSelectHub().getAt(0));
        }
        else {
            AppUserLogin appUserLogin = getSearchHubJfc().getSelected();
            if (appUserLogin != null) {
                if (getHub().contains(appUserLogin)) {
                    getHub().setAO(appUserLogin);
                }
            }
        }
    }
