    public OAButton createReportGenerateMethodButton() {
        OAButton cmd = new OAButton(getHub(), "Generate", getModel().getAllowMultiSelect() ? OAButton.HUB_METHOD : OAButton.OBJECT_METHOD) { 
            @Override
            public boolean onActionPerformed() throws Exception {
                if (ReportJfcBase.this.getModel().getAllowMultiSelect() && ReportJfcBase.this.getModel().getMultiSelectHub().getSize() > 0) {
                    for (Report report : ReportJfcBase.this.getModel().getMultiSelectHub()) {
                        if (!performAction(report)) return false;
                    }
                }
                else {
                    Report report = ReportJfcBase.this.getHub().getAO();
                    performAction(report);
                }
                return true;
            }
            boolean performAction(Report report) throws Exception {
                if (report == null) return false;
                report.generate();
                return true;
            }
        };
        cmd.setIcon(Resource.getJarIcon("command16.png"));
        cmd.setMethodName(Report.M_Generate);
        cmd.setup();
        return cmd;
    }
    
